import React, { useState } from "react";
import WeatherIcon from "./WeatherIcon";

import "./Forecast.css";
import axios from "axios";

export default function Forecast(props) {
  const [loaded, setLoaded] = useState(false);
  const [forecast, setForecast] = useState(null);

  function handleResponse(response) {
    setForecast(response.data.daily);
    setLoaded(true);
  }
  function load() {
    let apiKey = `8d9838178b5b401f1b4e7cb5af18e210`;
    let latitude = props.coordinate.lon;
    let longitude = props.coordinate.lat;
    let apiUrl = `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&appid=${apiKey}`;

    axios.get(apiUrl).then(handleResponse);
  }

  if (loaded) {
    return (
      <div className="Forecast">
        <div className="row">
          <div className="col">
            <div className="Forecast-day">{forecast[0].dt}</div>
            <WeatherIcon code={forecast[0].weather[0].icon} size={20} />
            <div className="Forecast-temperature">
              <span className="Forecast-max-temp">
                {Math.round(forecast[0].temp.max)}°{" "}
              </span>
              <span className="Forecast-min-temp">
                {" "}
                {Math.round(forecast[0].temp.min)}°{" "}
              </span>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    load();

    return null;
  }
}
