import React, { useEffect, useState } from "react";
import "./Forecast.css";
import axios from "axios";
import ForecastDay from "./ForecastDay";

export default function Forecast(props) {
  const [loaded, setLoaded] = useState(false);
  const [forecast, setForecast] = useState(null);

  useEffect(() => {
    setLoaded(false);
  }, [props.coordinate]);

  async function makeApiRequest() {
    let apiKey = `8d9838178b5b401f1b4e7cb5af18e210`;
    let latitude = props.coordinate.lon;
    let longitude = props.coordinate.lat;
    let apiUrl = `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&appid=${apiKey}`;

    try {
      const response = await axios.get(apiUrl);
      handleResponse(response);
    } catch (error) {
      if (error.response && error.response.status === 429) {
        // Retry with exponential backoff
        await retryWithExponentialBackoff(makeApiRequest);
      } else {
        // Handle other errors
        console.error("Failed to make API request:", error);
      }
    }
  }

  async function retryWithExponentialBackoff(requestFunction, retryCount = 0) {
    try {
      const response = await requestFunction();
      return response;
    } catch (error) {
      if (error.response && error.response.status === 429 && retryCount < 3) {
        // Retry with exponential backoff
        const delay = 2 ** retryCount * 1000; // Exponential backoff formula
        await new Promise((resolve) => setTimeout(resolve, delay));
        return retryWithExponentialBackoff(requestFunction, retryCount + 1);
      } else {
        // Give up after max retries or handle other errors
        console.error("Failed to make API request:", error);
        throw error;
      }
    }
  }

  function handleResponse(response) {
    setForecast(response.data.daily);
    setLoaded(true);
  }

  if (loaded) {
    return (
      <div className="Forecast">
        <div className="row">
          {forecast.map(function (dailyForecast, index) {
            if (index < 6) {
              return (
                <div className="col" key={index}>
                  <ForecastDay data={dailyForecast} />
                </div>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
    );
  } else {
    return null;
  }
}
